import React, { useState } from 'react';
import Editor from './Editor';

function App() {
  const[html, setHtml] = useState("")
  const[css, setCss] = useState("")
  const[js, setJs] = useState("") 

  return (
    <>
    <div className='top-pane'>
      <Editor 
        language = 'xml'
        displayName='HTML'
        value={html}
        onChange={setHtml}
      />
      <Editor 
        language = 'css'
        displayName='CSS'
        value={css}
        onChange={setCss}
      />
      <Editor
        language = 'javascript'
        displayName='JS'
        value={js}
        onChange={setJs}
      />
    </div>
    <div className='pane'>
      <iframe 
        title='output'
        sandbox='allow-scripts'  //for security reasons to avoid cookies of iframe
        width="100%"
        height="100%"

      />
    </div>
    </>
  )
}

export default App;
